import logo from './logo.svg';
import './App.css';
import React from 'react';

  // CLASS ClassComponentGreeting

class ClassComponentGreeting extends React.Component {
  render() {
    return (<h2 style={{color: "green"}}>Greeting from Class Component</h2>);
  }
}

  // FUNCTION FunctionComponentGreeting

function FunctionComponentGreeting() {
  return (
    <h3 style={{color: "yellow"}}>Greetings from Function Component</h3>
  )
}

  // CONSTANT ConstantFunctionGreeting

const ConstantFunctionGreeting = () => {
  return (
    <h3 style={{color: "red"}}>Greetings from Constant Function Component</h3>
  )
}


  // FUNCTION APP
function App() {
  // START BUSINESS LOGIC

  const stringElement = "Hello String Element";
  const jsxElement = <h2>Hello JSX Syntax</h2>;
  const jsxVariableEl = <h2>JSX {stringElement}</h2>

  //JSX & FUNCTIONS

  const firstName = "Stjepan"
  const lastName = "Goreta"

  const jsxFunctionElement = <h2>Hello {fullName()}</h2>
  const jsxName = <h2>Hello {fullName2("Jasmin", "Causevic")}</h2>

  function fullName() {
    return firstName + " " + lastName;
  }

  function fullName2(fname, lname) {
    return fname + " " + lname;
  }

  // END OF BUSINESS LOGIC
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <h2>{stringElement}</h2>
        {jsxElement}
        {jsxVariableEl}
        {jsxFunctionElement}
        {jsxName}
        <ClassComponentGreeting/>
        <FunctionComponentGreeting/>
        <ConstantFunctionGreeting/>
        <MyGoal/>
