import { useState } from "react";

function ArticleComponent (props) {
    

    function changePage() {
        if (currentPage + 1 < Object.keys(props.articles).length) {
            setCurrentPage(currentPage + 1);
            console.log(currentPage);
        } else {
            alert("No more articles to show");
        }
    }
    

    return (
        <div className="right">
            <button onClick={() => changePage()}>Next Article</button>
            <h2>{props.articles[currentPage].name}</h2>
            <p>{props.articles[currentPage].description}</p>
        </div>
    )
}

export {ArticleComponent};
